mapper.select:
SELECT COUNT(*)
FROM SUMMONER_MATCH_INFO
WHERE ACCOUNT_ID = #{accountId}
AND   GAME_ID = #{gameId}

SELECT COUNT(*)
FROM MATCH_BASIC
WHERE GAME_ID = #{gameId}

